import { useState } from "react";
import BookCreate from "./components/BookCreate";
import BookList from "./components/BookList";
function App() {

    const [books, setBooks] = useState([]);

    const handleCreateBook = (title) => {

        const updCreateBk = [...books, { id: Math.round(Math.random() * 999), title: title }];
        setBooks(updCreateBk);

        // console.log('Need to add book :', title)

    };

    const delBookByID = (id) => {
        const updDelBk = books.filter((book) => {
            return book.id !== id;
        });
        setBooks(updDelBk);
    };

    const editBookByID = (id, title) => {
        const updEditBk = books.map((book) => {

            if (book.id === id) {
                return { ...book, title: title };
            }
            return book;

        });
        setBooks(updEditBk);
    }
    return (
        <div className="app">
            {/* {books.length} */}
            <BookCreate onCreate={handleCreateBook} />
            <BookList onEditBook={editBookByID} onDelete={delBookByID} books={books} />
        </div>
    );
}

export default App;
